void quo_WISH(void)
{
    int i, j, k = 0, x[20];
    for (i = 0;i < 20;i++)
    {
        printf("\n\n\n\n\n\n\n\n                                              新年已至，我祝愿您:");
        x[i] = rand() % 20;
        for (j = 0;j < i;j++)
        {
            if (x[j] == x[i])
            x[i] = rand() % 20;
        }
        WISH(x[i]);
        system("clear");
    }
    for (i = 0, j = 0;j < 15;)
    {
        printf("\n\n\n\n\n\n\n\n                                              新年已至，我祝愿您:");
        WISH(x[i]);
        i++;
        if (i == 20)
        {
            i = 0;
            j++;
        }
        system("clear");
    }
    for (i = 0;i < 3;i++)
    {
        printf("\n\n\n\n\n\n\n\n                                         新年已至，新的一年里，您必将:");
        WISH(x[i]);
        usleep(1000000);
        system("clear");
    }
}
