void quo_WISH(void)
{
    int i, j, k = 0, x[20];
    for (i = 0; i < 20; i++)
    {
        color(7);
        printf("新年已至，我祝愿您:");
        x[i] = rand() % 20;
        for (j = 0; j < i; j++)
        {
            if (x[j] == x[i])
                x[i] = rand() % 20;
        }
        k = rand() % 15 + 1;
        color(k);
        WISH(x[i]);
        system("cls");
    }
    for (i = 0, j = 0; j < 2;)
    {
        color(7);
        printf("新年已至，我祝愿您:");
        k = rand() % 15 + 1;
        color(k);
        WISH(x[i]);
        i++;
        if (i == 20)
        {
            i = 0;
            j++;
        }
        system("cls");
    }
    for (i = 0; i < 5; i++)
    {
        color(12);
        printf("新年已至，新的一年里，您必将:");
        WISH(x[i]);
        i++;
        Sleep(1000);
        system("cls");
    }
}
void color(const unsigned short textColor)
{
    if (textColor >= 0 && textColor <= 15)
        SetConsoleTextAttribute(GetStdHandle(STD_OUTPUT_HANDLE), textColor);
    else
        SetConsoleTextAttribute(GetStdHandle(STD_OUTPUT_HANDLE), 7);
}
